catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var CEP = g_form.getValue('cep');

    var gaRua = new GlideAjax('ViaCepIntegrationAjax');
    gaRua.addParam('sysparm_name', 'realizarRequisicao');
    gaRua.addParam('sysparm_cep', CEP);
    gaRua.getXML(callback);

    function callback(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');

        answer = JSON.parse(answer);

        if (answer) {
            g_form.setValue('logradouro', answer.logradouro);
			g_form.setValue('estado', answer.complemento);
            g_form.setValue('bairro', answer.bairro);
            g_form.setValue('cidade', answer.localidade);
            g_form.setValue('estado', answer.uf);			
        } 

    }
}
